import React, { Component } from "react";
import { Redirect, Link } from "react-router-dom";
import axios from "axios";
import "./style.css";
import PropTypes from "prop-types";
import { withStyles } from "@material-ui/core/styles";
import AppBar from "@material-ui/core/AppBar";
import Toolbar from "@material-ui/core/Toolbar";
import Typography from "@material-ui/core/Typography";
import Button from "@material-ui/core/Button";
import ExitToAppIcon from "@material-ui/icons/ExitToApp";
import AccountCircleIcon from "@material-ui/icons/AccountCircle";
import { withTheme } from "@material-ui/core/styles";

const styles = {
  root: {
    flexGrow: 1
  },
  grow: {
    flexGrow: 1
  },
  menuButton: {
    marginLeft: -12,
    marginRight: 20
  }
};

class Nav extends Component {
  constructor() {
    super();
    this.logout = this.logout.bind(this);
    this.state = {
      redirect: false,
      user: {}
    };
  }

  componentDidMount() {
    this.userInfo().then(response =>
      this.setState(
        {
          user: response.data.user
        },
        () => this.tester()
      )
    );
  }

  tester() {
    console.log(this.state.user);
  }

  userInfo() {
    return axios.get("/user/");
  }

  logout(event) {
    event.preventDefault();
    console.log("logging out");
    axios
      .post("/user/logout")
      .then(response => {
        console.log(response.data);
        this.setState({ redirect: true });
      })
      .catch(error => {
        console.log("Logout error");
      });
  }

  renderRedirect = () => {
    if (this.state.redirect) {
      return <Redirect to="/signin" />;
    }
  };

  render() {
    const { classes } = this.props;
    return (
      <div className={classes.root}>
        <AppBar
          position="static"
          style={{
            background: "#fff5b5"
          }}
          elevation={1}
        >
          <Toolbar
            style={{
              fontFamily: this.props.theme.typography.navFont
            }}
          >
            <Typography
              variant="h6"
              color="secondary"
              className={classes.grow}
              style={{ fontFamily: "Lobster", fontSize: "34px" }}
            >
              <a href="/searchresult">ParkHere</a>
            </Typography>
            <Button color="secondary" href="/searchresult">
              Search
            </Button>
            <Button color="secondary" href="/addlisting">
              Create
            </Button>

            {this.renderRedirect()}
            <Button color="secondary" title="Account" href="/profile">
              <AccountCircleIcon />
            </Button>
            <Button
              color="secondary"
              title="Logout"
              onClick={this.logout}
              href="/signin"
            >
              <ExitToAppIcon />
            </Button>
          </Toolbar>
        </AppBar>
      </div>
    );
  }
}

Nav.propTypes = {
  classes: PropTypes.object.isRequired
};

export default withStyles(styles)(withTheme(Nav));
